package it.fbonfadelli.algo.master.exercises.hash.tables

class NumberOfGoodWaysToSplitAString {

    companion object {
        private val numberOfGoodWaysToSplitAString = NumberOfGoodWaysToSplitAString()

        fun main() {
            println("NUMBER OF GOOD WAYS TO SPLIT A STRING")

            val inputs = listOf(
                "aacaba",
                "abcd",
                "a",
                "ab",
                "aaa",
                ""
            )

            inputs.forEachIndexed { index, input -> execute(index, input) }
        }

        private fun execute(index: Int, input: String) {
            println("CASE ${index+1}:")
            println("Input: $input")
            val output = numberOfGoodWaysToSplitAString.numSplits(input)
            println("Output: $output")
            println()
        }
    }

    fun numSplits(s: String): Int {
        if (s.length <= 1)
            return 0
        if (s.length==2) {
            return 1
        }

        val charFrequency: IntArray = createCharFrequency(s)
        var totalNumberOfDistinctLetters = totalNumberOfDistinctLetters(charFrequency)

        val left = IntArray(s.length+1)
        val right = IntArray(s.length+1)
        val leftSet = mutableSetOf<Char>()

        for (i in 0..s.length) {
            if (i == 0) {
                left[i] = 0
                right[i] = totalNumberOfDistinctLetters
            } else if (i == s.length) {
                left[i] = totalNumberOfDistinctLetters
                right[i] = 0
            } else {
                val c = s[i-1]
                charFrequency[c-'a']--
                if (charFrequency[c-'a'] == 0)
                    totalNumberOfDistinctLetters--

                left[i] = left[i-1] + if (leftSet.contains(c)) 0 else 1
                right[i] = totalNumberOfDistinctLetters
                leftSet.add(s[i-1])
            }
        }

        var count = 0
        for (i in 0..s.length-1) {
            if (left[i] == right[i])
                count++
        }
        return count
    }

    private fun createCharFrequency(s: String): IntArray {
        val arr = IntArray(26)
        for (c in s) {
            arr[c-'a']++
        }
        return arr
    }

    private fun totalNumberOfDistinctLetters(charMap: IntArray): Int {
        var count = 0
        for (i in 0..charMap.size-1) {
            if (charMap[i] > 0)
                count++
        }
        return count
    }

    private fun inefficient(s: String): Int {
        if (s.length <= 1)
            return 0
        if (s.length==2) {
            return 1
        }

        var count = 0

        for (i in 1..s.length-1) {
            val l = s.substring(0, i)
            val r = s.substring(i, s.length)
            if (countDistinctLetters(l) == countDistinctLetters(r))
                count++
        }

        return count
    }

    private fun countDistinctLetters(s: String): Int {
        return s.toCharArray().toSet().size
    }
}