package it.fbonfadelli.algo.master.hash.tables.exercises

class NumberOfGoodWaysToSplitAString {

    companion object {
        private val numberOfGoodWaysToSplitAString = NumberOfGoodWaysToSplitAString()

        fun main() {
            println("NUMBER OF GOOD WAYS TO SPLIT A STRING")

            val inputs = listOf(
                "aacaba",
                "abcd",
                "a",
                "ab",
                "aaa",
                ""
            )

            inputs.forEachIndexed { index, input -> execute(index, input) }
        }

        private fun execute(index: Int, input: String) {
            println("CASE ${index+1}:")
            println("Input: $input")
            val output = numberOfGoodWaysToSplitAString.execute(input)
            println("Output: $output")
            println()
        }
    }

    fun execute(s: String): Int {
        val leftFreq = IntArray(26)
        var leftDistinctChars = 0

        val rightFreq = IntArray(26)
        var rightDistinctChars = 0
        for (i in s.indices) {
            if (rightFreq[s[i]-'a'] == 0)
                rightDistinctChars++
            rightFreq[s[i]-'a']++
        }

        var goodSplits = 0
        for (i in s.indices) {
            if (leftFreq[s[i]-'a'] == 0)
                leftDistinctChars++
            leftFreq[s[i]-'a']++

            rightFreq[s[i]-'a']--
            if (rightFreq[s[i]-'a'] == 0)
                rightDistinctChars--

            if (leftDistinctChars == rightDistinctChars)
                goodSplits++
        }

        return goodSplits
    }
}